mapper.select:
select count(*) from materiel where delete_flag = 0
and type_id = #{typeId}
and name like CONCAT(CONCAT('%',#{name},'%'))

select m.*, t.type_name, s.supplier_name, k.kind_id, k.kind_name from ( select * from materiel where delete_flag = 0
and type_id = #{typeId}
and name like CONCAT(CONCAT('%',#{name},'%'))
) m left join goods_type t on m.type_id = t.type_id left join goods_kind k on t.kind_id = k.kind_id left join supplier s on m.supplier_id = s.supplier_id order by m.id desc
